mapper.select:
select
    ifnull(count(*),0) as total_count,
    ifnull(count(*),0) as total_count,
    ifnull(sum(price),0)  as total_amount
from  member_order
where  1=1

  and status = #{status}


  and create_time > #{sTime}


  and #{eTime} > create_time


  and first_charge_flag = #{firstChargeFlag}


  and referrer_id = #{referrerId}
